import { useState, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Upload, Trash2, Eye, ChevronLeft, ChevronRight, Link as LinkIcon } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

interface Image {
  id: number;
  url: string;
  alt: string;
}

interface ImageGalleryProps {
  images: Image[];
  onAddImage?: (url: string, alt: string) => void;
  onDeleteImage?: (id: number) => void;
  onReorder?: (images: Image[]) => void;
}

export function ImageGallery({
  images,
  onAddImage,
  onDeleteImage,
  onReorder,
}: ImageGalleryProps) {
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [draggedItem, setDraggedItem] = useState<number | null>(null);
  const [showUploadDialog, setShowUploadDialog] = useState(false);
  const [imageUrl, setImageUrl] = useState("");
  const [imageAlt, setImageAlt] = useState("");
  const [uploading, setUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleDragStart = (index: number) => {
    setDraggedItem(index);
  };

  const handleFileUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    setUploading(true);
    const reader = new FileReader();
    reader.onloadend = () => {
      const base64String = reader.result as string;
      setImageUrl(base64String);
      setImageAlt(file.name);
      setUploading(false);
    };
    reader.readAsDataURL(file);
  };

  const handleAddImage = () => {
    if (imageUrl && onAddImage) {
      onAddImage(imageUrl, imageAlt || "Image");
      setImageUrl("");
      setImageAlt("");
      setShowUploadDialog(false);
    }
  };

  const openUploadDialog = () => {
    setShowUploadDialog(true);
    setImageUrl("");
    setImageAlt("");
  };

  const handleDragOver = (e: React.DragEvent, index: number) => {
    e.preventDefault();
    if (draggedItem === null || draggedItem === index) return;

    const newImages = [...images];
    const [draggedImage] = newImages.splice(draggedItem, 1);
    newImages.splice(index, 0, draggedImage);
    onReorder?.(newImages);
    setDraggedItem(index);
  };

  const next = () => setSelectedIndex((i) => (i + 1) % images.length);
  const prev = () =>
    setSelectedIndex((i) => (i - 1 + images.length) % images.length);

  return (
    <div className="space-y-4">
      {/* Lightbox Preview */}
      {images.length > 0 && (
        <Card className="relative bg-muted aspect-video overflow-hidden flex items-center justify-center">
          <div className="w-full h-full bg-gradient-to-br from-muted to-muted/50 flex items-center justify-center">
            <div className="text-center">
              <Eye className="w-8 h-8 text-muted-foreground mx-auto mb-2" />
              <p className="text-sm text-muted-foreground">{images[selectedIndex]?.alt}</p>
            </div>
          </div>

          {images.length > 1 && (
            <>
              <Button
                variant="ghost"
                size="icon"
                className="absolute left-2 top-1/2 -translate-y-1/2"
                onClick={prev}
              >
                <ChevronLeft className="w-6 h-6" />
              </Button>
              <Button
                variant="ghost"
                size="icon"
                className="absolute right-2 top-1/2 -translate-y-1/2"
                onClick={next}
              >
                <ChevronRight className="w-6 h-6" />
              </Button>
              <div className="absolute bottom-2 left-1/2 -translate-x-1/2 text-xs bg-black/50 text-white px-2 py-1 rounded">
                {selectedIndex + 1} / {images.length}
              </div>
            </>
          )}
        </Card>
      )}

      {/* Thumbnails & Drag-drop */}
      <div className="space-y-2">
        <div className="flex justify-between items-center">
          <h4 className="font-medium text-sm">Gallery ({images.length})</h4>
          {onAddImage && (
            <Button size="sm" variant="outline" onClick={openUploadDialog} className="gap-2">
              <Upload className="w-4 h-4" /> Add Image
            </Button>
          )}
        </div>

        <div className="grid grid-cols-2 sm:grid-cols-4 gap-2">
          {images.map((img, idx) => (
            <div
              key={img.id}
              draggable
              onDragStart={() => handleDragStart(idx)}
              onDragOver={(e) => handleDragOver(e, idx)}
              onDragEnd={() => setDraggedItem(null)}
              className={`relative group aspect-square rounded-lg overflow-hidden border-2 transition-all cursor-move ${
                selectedIndex === idx
                  ? "border-primary"
                  : "border-border hover:border-primary/50"
              } ${draggedItem === idx ? "opacity-50" : ""}`}
              onClick={() => setSelectedIndex(idx)}
            >
              <div className="w-full h-full bg-gradient-to-br from-muted to-muted/50 flex items-center justify-center">
                <span className="text-xs text-muted-foreground text-center px-1">
                  {img.alt}
                </span>
              </div>
              <div className="absolute inset-0 bg-black/0 group-hover:bg-black/50 transition-colors flex items-center justify-center opacity-0 group-hover:opacity-100">
                {onDeleteImage && (
                  <Button
                    variant="destructive"
                    size="icon"
                    className="h-6 w-6"
                    onClick={(e) => {
                      e.stopPropagation();
                      onDeleteImage(img.id);
                    }}
                  >
                    <Trash2 className="w-3 h-3" />
                  </Button>
                )}
              </div>
            </div>
          ))}
        </div>
        <p className="text-xs text-muted-foreground">Drag to reorder images</p>
      </div>

      {/* Upload Dialog */}
      <Dialog open={showUploadDialog} onOpenChange={setShowUploadDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add Image</DialogTitle>
          </DialogHeader>
          <Tabs defaultValue="url">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="url">From URL</TabsTrigger>
              <TabsTrigger value="upload">Upload File</TabsTrigger>
            </TabsList>
            <TabsContent value="url" className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="image-url">Image URL</Label>
                <Input
                  id="image-url"
                  placeholder="https://example.com/image.jpg or /images/blog/bg-1.png"
                  value={imageUrl}
                  onChange={(e) => setImageUrl(e.target.value)}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="image-alt">Image Description</Label>
                <Input
                  id="image-alt"
                  placeholder="Description of the image"
                  value={imageAlt}
                  onChange={(e) => setImageAlt(e.target.value)}
                />
              </div>
              {imageUrl && (
                <div className="border rounded-md overflow-hidden">
                  <img src={imageUrl} alt="Preview" className="w-full h-40 object-cover" />
                </div>
              )}
            </TabsContent>
            <TabsContent value="upload" className="space-y-4">
              <div className="space-y-2">
                <Label>Upload Image</Label>
                <div className="border-2 border-dashed rounded-lg p-8 text-center cursor-pointer hover:border-primary/50" onClick={() => fileInputRef.current?.click()}>
                  <Upload className="w-8 h-8 mx-auto mb-2 text-muted-foreground" />
                  <p className="text-sm text-muted-foreground">Click to upload or drag and drop</p>
                  <p className="text-xs text-muted-foreground mt-1">PNG, JPG, GIF, WebP (Max 10MB)</p>
                </div>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  onChange={handleFileUpload}
                  className="hidden"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="upload-alt">Image Description</Label>
                <Input
                  id="upload-alt"
                  placeholder="Description of the image"
                  value={imageAlt}
                  onChange={(e) => setImageAlt(e.target.value)}
                />
              </div>
              {imageUrl && (
                <div className="border rounded-md overflow-hidden">
                  <img src={imageUrl} alt="Preview" className="w-full h-40 object-cover" />
                </div>
              )}
            </TabsContent>
          </Tabs>
          <div className="flex gap-2 justify-end">
            <Button variant="outline" onClick={() => setShowUploadDialog(false)}>
              Cancel
            </Button>
            <Button onClick={handleAddImage} disabled={!imageUrl || uploading}>
              {uploading ? "Uploading..." : "Add Image"}
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/admin/ExportImport.tsx.content:
import { AdminLayout } from "@/layouts/AdminLayout";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Download, Upload, FileJson, Loader2, CheckCircle, AlertTriangle } from "lucide-react";
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";

interface Exportable {
  id: string;
  name: string;
  description: string;
  endpoint: string;
  icon: React.ReactNode;
}

export default function AdminExportImport() {
  const { toast } = useToast();
  const [exportLoading, setExportLoading] = useState<string | null>(null);
  const [importLoading, setImportLoading] = useState(false);
  const [recentExports, setRecentExports] = useState<any[]>([]);

  const exportables: Exportable[] = [
    {
      id: "posts",
      name: "Posts",
      description: "All blog posts and articles",
      endpoint: "/api/posts",
      icon: <FileJson className="w-5 h-5" />,
    },
    {
      id: "projects",
      name: "Projects",
      description: "All portfolio projects",
      endpoint: "/api/projects",
      icon: <FileJson className="w-5 h-5" />,
    },
    {
      id: "skills",
      name: "Skills",
      description: "All technical skills",
      endpoint: "/api/skills",
      icon: <FileJson className="w-5 h-5" />,
    },
    {
      id: "services",
      name: "Services",
      description: "All offered services",
      endpoint: "/api/services",
      icon: <FileJson className="w-5 h-5" />,
    },
    {
      id: "testimonials",
      name: "Testimonials",
      description: "All client testimonials",
      endpoint: "/api/testimonials",
      icon: <FileJson className="w-5 h-5" />,
    },
    {
      id: "settings",
      name: "Settings",
      description: "Site configuration and settings",
      endpoint: "/api/settings",
      icon: <FileJson className="w-5 h-5" />,
    },
  ];

  const handleExport = async (exportable: Exportable) => {
    setExportLoading(exportable.id);
    try {
      const res = await fetch(exportable.endpoint, { credentials: "include" });
      if (!res.ok) throw new Error("Failed to fetch data");
      const data = await res.json();

      const jsonString = JSON.stringify(data, null, 2);
      const blob = new Blob([jsonString], { type: "application/json" });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${exportable.id}_export_${new Date().toISOString().split("T")[0]}.json`;
      a.click();
      window.URL.revokeObjectURL(url);

      const newExport = {
        name: a.download,
        size: (blob.size / 1024).toFixed(2) + " KB",
        date: new Date().toLocaleDateString(),
        timestamp: new Date(),
      };

      setRecentExports((prev) => [newExport, ...prev].slice(0, 10));
      toast({
        title: "Success",
        description: `Exported ${exportable.name} successfully`,
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to export",
        variant: "destructive",
      });
    } finally {
      setExportLoading(null);
    }
  };

  const handleImport = async (file: File) => {
    setImportLoading(true);
    try {
      const fileContent = await file.text();
      const data = JSON.parse(fileContent);

      const fileName = file.name.toLowerCase();
      let endpoint = "/api/posts";

      if (fileName.includes("project")) endpoint = "/api/projects";
      else if (fileName.includes("skill")) endpoint = "/api/skills";
      else if (fileName.includes("service")) endpoint = "/api/services";
      else if (fileName.includes("testimonial")) endpoint = "/api/testimonials";
      else if (fileName.includes("setting")) endpoint = "/api/settings";

      if (Array.isArray(data)) {
        let successCount = 0;
        for (const item of data) {
          try {
            const res = await fetch(endpoint, {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              credentials: "include",
              body: JSON.stringify(item),
            });
            if (res.ok) successCount++;
          } catch (e) {
            console.error("Failed to import item:", e);
          }
        }
        toast({
          title: "Import Complete",
          description: `Successfully imported ${successCount} out of ${data.length} items`,
        });
      } else {
        const res = await fetch(endpoint, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          credentials: "include",
          body: JSON.stringify(data),
        });
        if (res.ok) {
          toast({
            title: "Success",
            description: "Data imported successfully",
          });
        }
      }
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to import file",
        variant: "destructive",
      });
    } finally {
      setImportLoading(false);
    }
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      if (!file.name.endsWith(".json")) {
        toast({
          title: "Error",
          description: "Please upload a JSON file",
          variant: "destructive",
        });
        return;
      }
      handleImport(file);
    }
  };

  return (
    <AdminLayout>
      <div className="space-y-8">
        <div>
          <h1 className="text-3xl font-heading font-bold">Export & Import Data</h1>
          <p className="text-muted-foreground">
            Backup and restore your content data
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Export Section */}
          <div className="space-y-4">
            <h2 className="text-xl font-semibold">Export Data</h2>
            <div className="space-y-3">
              {exportables.map((exportable) => (
                <Card key={exportable.id}>
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between gap-4">
                      <div className="flex items-start gap-3 flex-1 min-w-0">
                        <div className="p-2 bg-primary/10 rounded flex-shrink-0">
                          {exportable.icon}
                        </div>
                        <div className="min-w-0">
                          <h3 className="font-semibold text-sm">{exportable.name}</h3>
                          <p className="text-xs text-muted-foreground">
                            {exportable.description}
                          </p>
                        </div>
                      </div>
                      <Button
                        size="sm"
                        disabled={exportLoading === exportable.id}
                        onClick={() => handleExport(exportable)}
                        data-testid={`button-export-${exportable.id}`}
                        className="flex-shrink-0 gap-1"
                      >
                        {exportLoading === exportable.id ? (
                          <Loader2 className="w-3 h-3 animate-spin" />
                        ) : (
                          <Download className="w-3 h-3" />
                        )}
                        Export
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>

          {/* Import Section */}
          <div className="space-y-4">
            <h2 className="text-xl font-semibold">Import Data</h2>
            <Card>
              <CardContent className="p-6">
                <label className="cursor-pointer block">
                  <div className="border-2 border-dashed border-muted-foreground/20 rounded-lg p-8 flex flex-col items-center justify-center hover:border-primary/50 hover:bg-primary/5 transition-all">
                    {importLoading ? (
                      <>
                        <Loader2 className="w-12 h-12 text-primary mb-3 animate-spin" />
                        <h3 className="font-semibold mb-1">Importing...</h3>
                      </>
                    ) : (
                      <>
                        <Upload className="w-12 h-12 text-muted-foreground mb-3" />
                        <h3 className="font-semibold mb-1">Drop JSON file or click</h3>
                        <p className="text-xs text-muted-foreground text-center">
                          Supported: JSON files from posts, projects, skills, services, or testimonials
                        </p>
                      </>
                    )}
                  </div>
                  <input
                    type="file"
                    accept=".json"
                    onChange={handleFileSelect}
                    disabled={importLoading}
                    className="hidden"
                    data-testid="input-file-import"
                  />
                </label>
                <div className="mt-4 p-3 bg-blue-500/10 border border-blue-500/20 rounded-lg">
                  <p className="text-xs text-muted-foreground">
                    ðŸ’¡ Export files automatically detect the data type and import to the correct location.
                  </p>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>

        {/* Recent Exports */}
        {recentExports.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Recent Exports</CardTitle>
              <CardDescription>
                Last {recentExports.length} exported files
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                {recentExports.map((file, i) => (
                  <div
                    key={i}
                    className="flex items-center justify-between p-3 border rounded-lg"
                  >
                    <div className="flex items-center gap-3 flex-1 min-w-0">
                      <CheckCircle className="w-4 h-4 text-green-500 flex-shrink-0" />
                      <div className="min-w-0 flex-1">
                        <p className="font-medium text-sm truncate">{file.name}</p>
                        <p className="text-xs text-muted-foreground">
                          {file.date} â€¢ {file.size}
                        </p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Info Section */}
        <Card className="border-blue-500/20 bg-blue-500/5">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <AlertTriangle className="w-5 h-5 text-blue-500" />
              Export & Import Guide
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-2 text-sm text-muted-foreground">
            <p>
              â€¢ Export your data as JSON format for backup or migration purposes
            </p>
            <p>â€¢ Import JSON files to add or update your content data</p>
            <p>â€¢ Files are detected by name - use original export names when possible</p>
            <p>
              â€¢ Each export includes a timestamp for easy version control
            </p>
            <p>â€¢ All imports are validated before processing</p>
          </CardContent>
        </Card>
      </div>
    </AdminLayout>
  );
}

client/src/pages/admin/LanguageManager.t.content:
import { AdminLayout } from "@/layouts/AdminLayout";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Languages, Plus, Download, Upload, Trash2, Edit2, Check, X, Globe, Search
} from "lucide-react";
import { useState, useRef } from "react";
import { useLanguage, LanguageData, TranslationData } from "@/context/LanguageContext";
import { useToast } from "@/hooks/use-toast";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";

export default function LanguageManager() {
  const { 
    languages, 
    currentLanguage, 
    setCurrentLanguage, 
    addLanguage, 
    removeLanguage, 
    updateTranslations,
    exportLanguage,
    importLanguage 
  } = useLanguage();
  const { toast } = useToast();
  const fileInputRef = useRef<HTMLInputElement>(null);

  const [newLangCode, setNewLangCode] = useState("");
  const [newLangName, setNewLangName] = useState("");
  const [newLangNative, setNewLangNative] = useState("");
  const [editingKey, setEditingKey] = useState<string | null>(null);
  const [editingValue, setEditingValue] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedLang, setSelectedLang] = useState<string>(languages[0]?.code || "en");
  const [newKeyDialog, setNewKeyDialog] = useState(false);
  const [newKey, setNewKey] = useState("");
  const [newTranslations, setNewTranslations] = useState<Record<string, string>>({});

  const handleAddLanguage = () => {
    if (!newLangCode || !newLangName) {
      toast({ title: "Error", description: "Please fill in all required fields", variant: "destructive" });
      return;
    }

    const baseTranslations = languages.find(l => l.code === "en")?.translations || {};

    addLanguage({
      code: newLangCode.toLowerCase(),
      name: newLangName,
      nativeName: newLangNative || newLangName,
      translations: { ...baseTranslations },
    });

    toast({ title: "Success", description: `Language "${newLangName}" added successfully` });
    setNewLangCode("");
    setNewLangName("");
    setNewLangNative("");
  };

  const handleExport = (code: string) => {
    const data = exportLanguage(code);
    const blob = new Blob([data], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `language_${code}.json`;
    a.click();
    URL.revokeObjectURL(url);
    toast({ title: "Exported", description: `Language file exported successfully` });
  };

  const handleImport = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const content = event.target?.result as string;
      if (importLanguage(content)) {
        toast({ title: "Imported", description: "Language imported successfully" });
      } else {
        toast({ title: "Error", description: "Invalid language file format", variant: "destructive" });
      }
    };
    reader.readAsText(file);
    e.target.value = "";
  };

  const handleSaveTranslation = (langCode: string, key: string, value: string) => {
    updateTranslations(langCode, { [key]: value });
    setEditingKey(null);
    setEditingValue("");
    toast({ title: "Saved", description: "Translation updated" });
  };

  const handleAddKey = () => {
    if (!newKey) return;

    languages.forEach(lang => {
      updateTranslations(lang.code, { [newKey]: newTranslations[lang.code] || newKey });
    });

    setNewKeyDialog(false);
    setNewKey("");
    setNewTranslations({});
    toast({ title: "Added", description: "New translation key added" });
  };

  const filteredKeys = Object.keys(languages.find(l => l.code === selectedLang)?.translations || {})
    .filter(key => 
      key.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (languages.find(l => l.code === selectedLang)?.translations[key] || "")
        .toLowerCase().includes(searchQuery.toLowerCase())
    );

  return (
    <AdminLayout>
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-heading font-bold flex items-center gap-2">
              <Languages className="w-8 h-8" /> Language Manager
            </h1>
            <p className="text-muted-foreground">Manage translations and language settings</p>
          </div>
          <div className="flex gap-2">
            <input 
              type="file" 
              ref={fileInputRef} 
              className="hidden" 
              accept=".json"
              onChange={handleImport}
            />
            <Button variant="outline" onClick={() => fileInputRef.current?.click()}>
              <Upload className="w-4 h-4 mr-2" /> Import
            </Button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <Card className="lg:col-span-1">
            <CardHeader>
              <CardTitle>Languages</CardTitle>
              <CardDescription>Available languages in your site</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                {languages.map((lang) => (
                  <div 
                    key={lang.code}
                    className={`flex items-center justify-between p-3 border rounded-lg cursor-pointer transition-colors ${
                      selectedLang === lang.code ? "border-primary bg-primary/5" : "hover:border-primary/50"
                    }`}
                    onClick={() => setSelectedLang(lang.code)}
                  >
                    <div className="flex items-center gap-3">
                      <Globe className="w-5 h-5 text-muted-foreground" />
                      <div>
                        <p className="font-medium">{lang.name}</p>
                        <p className="text-sm text-muted-foreground">{lang.nativeName} ({lang.code})</p>
                      </div>
                    </div>
                    <div className="flex items-center gap-2">
                      {currentLanguage === lang.code && (
                        <Badge variant="secondary">Active</Badge>
                      )}
                      <Button 
                        variant="ghost" 
                        size="icon"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleExport(lang.code);
                        }}
                      >
                        <Download className="w-4 h-4" />
                      </Button>
                      {lang.code !== "en" && (
                        <Button 
                          variant="ghost" 
                          size="icon"
                          className="text-destructive"
                          onClick={(e) => {
                            e.stopPropagation();
                            removeLanguage(lang.code);
                            toast({ title: "Removed", description: `Language "${lang.name}" removed` });
                          }}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      )}
                    </div>
                  </div>
                ))}
              </div>

              <div className="pt-4 border-t space-y-3">
                <h4 className="font-medium text-sm">Add New Language</h4>
                <div className="space-y-2">
                  <Input 
                    placeholder="Language code (e.g. fr, de, ja)" 
                    value={newLangCode}
                    onChange={(e) => setNewLangCode(e.target.value)}
                  />
                  <Input 
                    placeholder="Language name (e.g. French)" 
                    value={newLangName}
                    onChange={(e) => setNewLangName(e.target.value)}
                  />
                  <Input 
                    placeholder="Native name (e.g. FranÃ§ais)" 
                    value={newLangNative}
                    onChange={(e) => setNewLangNative(e.target.value)}
                  />
                  <Button className="w-full" onClick={handleAddLanguage}>
                    <Plus className="w-4 h-4 mr-2" /> Add Language
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="lg:col-span-2">
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle>Translations</CardTitle>
                  <CardDescription>
                    Edit translations for {languages.find(l => l.code === selectedLang)?.name}
                  </CardDescription>
                </div>
                <Dialog open={newKeyDialog} onOpenChange={setNewKeyDialog}>
                  <DialogTrigger asChild>
                    <Button size="sm">
                      <Plus className="w-4 h-4 mr-2" /> Add Key
                    </Button>
                  </DialogTrigger>
                  <DialogContent>
                    <DialogHeader>
                      <DialogTitle>Add Translation Key</DialogTitle>
                      <DialogDescription>
                        Add a new translation key and its values for all languages
                      </DialogDescription>
                    </DialogHeader>
                    <div className="space-y-4 py-4">
                      <div className="space-y-2">
                        <Label>Translation Key</Label>
                        <Input 
                          placeholder="e.g. nav.newPage"
                          value={newKey}
                          onChange={(e) => setNewKey(e.target.value)}
                        />
                      </div>
                      {languages.map(lang => (
                        <div key={lang.code} className="space-y-2">
                          <Label>{lang.name} ({lang.code})</Label>
                          <Input 
                            placeholder={`Translation in ${lang.name}`}
                            value={newTranslations[lang.code] || ""}
                            onChange={(e) => setNewTranslations(prev => ({
                              ...prev,
                              [lang.code]: e.target.value
                            }))}
                          />
                        </div>
                      ))}
                    </div>
                    <DialogFooter>
                      <Button variant="outline" onClick={() => setNewKeyDialog(false)}>
                        Cancel
                      </Button>
                      <Button onClick={handleAddKey}>Add Key</Button>
                    </DialogFooter>
                  </DialogContent>
                </Dialog>
              </div>
            </CardHeader>
            <CardContent>
              <div className="mb-4">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                  <Input 
                    placeholder="Search translations..."
                    className="pl-9"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                  />
                </div>
              </div>

              <div className="space-y-2 max-h-[500px] overflow-y-auto pr-2">
                {filteredKeys.map((key) => {
                  const lang = languages.find(l => l.code === selectedLang);
                  const value = lang?.translations[key] || "";
                  const isEditing = editingKey === key;

                  return (
                    <div key={key} className="p-3 border rounded-lg">
                      <div className="flex items-center justify-between mb-2">
                        <code className="text-xs bg-muted px-2 py-1 rounded font-mono">{key}</code>
                        {!isEditing && (
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={() => {
                              setEditingKey(key);
                              setEditingValue(value);
                            }}
                          >
                            <Edit2 className="w-4 h-4" />
                          </Button>
                        )}
                      </div>
                      {isEditing ? (
                        <div className="flex gap-2">
                          <Input 
                            value={editingValue}
                            onChange={(e) => setEditingValue(e.target.value)}
                            className="flex-1"
                          />
                          <Button 
                            size="icon" 
                            variant="ghost"
                            onClick={() => handleSaveTranslation(selectedLang, key, editingValue)}
                          >
                            <Check className="w-4 h-4 text-green-500" />
                          </Button>
                          <Button 
                            size="icon" 
                            variant="ghost"
                            onClick={() => {
                              setEditingKey(null);
                              setEditingValue("");
                            }}
                          >
                            <X className="w-4 h-4 text-red-500" />
                          </Button>
                        </div>
                      ) : (
                        <p className="text-sm">{value}</p>
                      )}
                    </div>
                  );
                })}
              </div>
            </CardContent>
          </Card>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Default Language</CardTitle>
            <CardDescription>Set the default language for your site</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex flex-wrap gap-2">
              {languages.map((lang) => (
                <Button
                  key={lang.code}
                  variant={currentLanguage === lang.code ? "default" : "outline"}
                  onClick={() => {
                    setCurrentLanguage(lang.code);
                    toast({ title: "Default Changed", description: `Default language set to ${lang.name}` });
                  }}
                >
                  <Globe className="w-4 h-4 mr-2" />
                  {lang.name}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    </AdminLayout>
  );
}

server/storage.ts.content:
import {
  users, type User, type InsertUser,
  projects, type Project, type InsertProject,
  posts, type Post, type InsertPost,
  pages, type Page, type InsertPage,
  skills, type Skill, type InsertSkill,
  services, type Service, type InsertService,
  testimonials, type Testimonial, type InsertTestimonial,
  messages, type Message, type InsertMessage,
  activityLogs, type ActivityLog, type InsertActivityLog,
  notifications, type Notification, type InsertNotification,
  siteSettings, type SiteSetting, type InsertSiteSetting,
  categories, type Category, type InsertCategory,
  media, type Media, type InsertMedia,
  comments, type Comment, type InsertComment,
  reviews, type Review, type InsertReview,
  securitySettings, type SecuritySetting, type InsertSecuritySetting,
  trustedDevices, type TrustedDevice, type InsertTrustedDevice,
  userSessions, type UserSession, type InsertUserSession,
  ipRules, type IpRule, type InsertIpRule,
  securityLogs, type SecurityLog, type InsertSecurityLog,
  homepageSections, type HomepageSection, type InsertHomepageSection,
  faqs, type FAQ, type InsertFAQ,
  webauthnCredentials, type WebAuthnCredential, type InsertWebAuthnCredential
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, asc, and, or, ilike, sql } from "drizzle-orm";

export interface IStorage {
  // Users
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  getUserByUsernameOrEmail(credential: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: number, user: Partial<InsertUser>): Promise<User | undefined>;
  deleteUser(id: number): Promise<boolean>;
  getAllUsers(): Promise<User[]>;

  // Projects
  getProject(id: number): Promise<Project | undefined>;
  getAllProjects(): Promise<Project[]>;
  getPublishedProjects(): Promise<Project[]>;
  createProject(project: InsertProject): Promise<Project>;
  updateProject(id: number, project: Partial<InsertProject>): Promise<Project | undefined>;
  deleteProject(id: number): Promise<boolean>;
  incrementProjectViews(id: number): Promise<void>;

  // Posts
  getPost(id: number): Promise<Post | undefined>;
  getPostBySlug(slug: string): Promise<Post | undefined>;
  getAllPosts(): Promise<Post[]>;
  getPublishedPosts(): Promise<Post[]>;
  createPost(post: InsertPost): Promise<Post>;
  updatePost(id: number, post: Partial<InsertPost>): Promise<Post | undefined>;
  deletePost(id: number): Promise<boolean>;
  incrementPostViews(id: number): Promise<void>;

  // Pages
  getPage(id: number): Promise<Page | undefined>;
  getPageBySlug(slug: string): Promise<Page | undefined>;
  getAllPages(): Promise<Page[]>;
  getPublishedPages(): Promise<Page[]>;
  createPage(page: InsertPage): Promise<Page>;
  updatePage(id: number, page: Partial<InsertPage>): Promise<Page | undefined>;
  deletePage(id: number): Promise<boolean>;
  incrementPageViews(id: number): Promise<void>;

  // Skills
  getSkill(id: number): Promise<Skill | undefined>;
  getAllSkills(): Promise<Skill[]>;
  createSkill(skill: InsertSkill): Promise<Skill>;
  updateSkill(id: number, skill: Partial<InsertSkill>): Promise<Skill | undefined>;
  deleteSkill(id: number): Promise<boolean>;

  // Services
  getService(id: number): Promise<Service | undefined>;
  getAllServices(): Promise<Service[]>;
  getActiveServices(): Promise<Service[]>;
  createService(service: InsertService): Promise<Service>;
  updateService(id: number, service: Partial<InsertService>): Promise<Service | undefined>;
  deleteService(id: number): Promise<boolean>;

  // Testimonials
  getTestimonial(id: number): Promise<Testimonial | undefined>;
  getAllTestimonials(): Promise<Testimonial[]>;
  getActiveTestimonials(): Promise<Testimonial[]>;
  createTestimonial(testimonial: InsertTestimonial): Promise<Testimonial>;
  updateTestimonial(id: number, testimonial: Partial<InsertTestimonial>): Promise<Testimonial | undefined>;
  deleteTestimonial(id: number): Promise<boolean>;

  // Messages
  getMessage(id: number): Promise<Message | undefined>;
  getAllMessages(): Promise<Message[]>;
  getUnreadMessages(): Promise<Message[]>;
  createMessage(message: InsertMessage): Promise<Message>;
  markMessageAsRead(id: number): Promise<boolean>;
  archiveMessage(id: number): Promise<boolean>;
  deleteMessage(id: number): Promise<boolean>;

  // Activity Logs
  getAllActivityLogs(limit?: number): Promise<ActivityLog[]>;
  createActivityLog(log: InsertActivityLog): Promise<ActivityLog>;

  // Notifications
  getAllNotifications(userId?: number): Promise<Notification[]>;
  createNotification(notification: InsertNotification): Promise<Notification>;
  markNotificationAsRead(id: number): Promise<boolean>;

  // Homepage Sections
  getAllHomepageSections(): Promise<HomepageSection[]>;
  getHomepageSectionByName(name: string): Promise<HomepageSection | undefined>;
  updateHomepageSection(name: string, section: Partial<InsertHomepageSection>): Promise<HomepageSection | undefined>;
  upsertHomepageSection(name: string, section: Partial<InsertHomepageSection>): Promise<HomepageSection>;

  // Site Settings
  getSetting(key: string): Promise<SiteSetting | undefined>;
  getAllSettings(): Promise<SiteSetting[]>;
  upsertSetting(key: string, value: any): Promise<SiteSetting>;

  // Categories
  getCategory(id: number): Promise<Category | undefined>;
  getCategoriesByType(type: string): Promise<Category[]>;
  createCategory(category: InsertCategory): Promise<Category>;
  updateCategory(id: number, category: Partial<InsertCategory>): Promise<Category | undefined>;
  deleteCategory(id: number): Promise<boolean>;

  // Media
  getMedia(id: number): Promise<Media | undefined>;
  getAllMedia(): Promise<Media[]>;
  createMedia(mediaItem: InsertMedia): Promise<Media>;
  deleteMedia(id: number): Promise<boolean>;

  // Comments
  getComment(id: number): Promise<Comment | undefined>;
  getAllComments(): Promise<Comment[]>;
  getCommentsByPost(postId: number): Promise<Comment[]>;
  getCommentsByProject(projectId: number): Promise<Comment[]>;
  getApprovedCommentsByPost(postId: number): Promise<Comment[]>;
  getApprovedCommentsByProject(projectId: number): Promise<Comment[]>;
  getPendingComments(): Promise<Comment[]>;
  getUnreadComments(): Promise<Comment[]>;
  getUnarchivedComments(): Promise<Comment[]>;
  createComment(comment: InsertComment): Promise<Comment>;
  updateComment(id: number, comment: Partial<InsertComment>): Promise<Comment | undefined>;
  approveComment(id: number): Promise<boolean>;
  markCommentAsRead(id: number): Promise<boolean>;
  archiveComment(id: number): Promise<boolean>;
  deleteComment(id: number): Promise<boolean>;

  // Reviews
  getReview(id: number): Promise<Review | undefined>;
  getAllReviews(): Promise<Review[]>;
  getReviewsByProject(projectId: number): Promise<Review[]>;
  getApprovedReviewsByProject(projectId: number): Promise<Review[]>;
  getPendingReviews(): Promise<Review[]>;
  getUnreadReviews(): Promise<Review[]>;
  getUnarchivedReviews(): Promise<Review[]>;
  createReview(review: InsertReview): Promise<Review>;
  updateReview(id: number, review: Partial<InsertReview>): Promise<Review | undefined>;
  approveReview(id: number): Promise<boolean>;
  markReviewAsRead(id: number): Promise<boolean>;
  archiveReview(id: number): Promise<boolean>;
  deleteReview(id: number): Promise<boolean>;

  // System
  getSystemStats(): Promise<{
    databaseSize: string;
    tableStats: { name: string; count: number }[];
    serverUptime: number;
    lastCheck: Date;
    status: "Healthy" | "Warning" | "Error";
  }>;
  getActivityLogs(limit: number, offset: number): Promise<ActivityLog[]>;
  clearActivityLogs(): Promise<void>;

  // Security Settings
  getSecuritySetting(key: string): Promise<SecuritySetting | undefined>;
  getAllSecuritySettings(): Promise<SecuritySetting[]>;
  upsertSecuritySetting(key: string, value: any): Promise<SecuritySetting>;

  // Trusted Devices
  getTrustedDevice(id: number): Promise<TrustedDevice | undefined>;
  getTrustedDevicesByUser(userId: number): Promise<TrustedDevice[]>;
  createTrustedDevice(device: InsertTrustedDevice): Promise<TrustedDevice>;
  updateTrustedDevice(id: number, device: Partial<InsertTrustedDevice>): Promise<TrustedDevice | undefined>;
  deleteTrustedDevice(id: number): Promise<boolean>;

  // User Sessions
  getUserSession(id: number): Promise<UserSession | undefined>;
  getUserSessionBySessionId(sessionId: string): Promise<UserSession | undefined>;
  getActiveSessionsByUser(userId: number): Promise<UserSession[]>;
  getAllActiveSessions(): Promise<UserSession[]>;
  createUserSession(session: InsertUserSession): Promise<UserSession>;
  updateUserSession(id: number, session: Partial<InsertUserSession>): Promise<UserSession | undefined>;
  terminateSession(id: number): Promise<boolean>;
  terminateAllUserSessions(userId: number): Promise<boolean>;
  terminateAllSessions(): Promise<boolean>;

  // IP Rules
  getIpRule(id: number): Promise<IpRule | undefined>;
  getIpRulesByType(type: string): Promise<IpRule[]>;
  getAllIpRules(): Promise<IpRule[]>;
  createIpRule(rule: InsertIpRule): Promise<IpRule>;
  deleteIpRule(id: number): Promise<boolean>;
  isIpWhitelisted(ip: string): Promise<boolean>;
  isIpBlacklisted(ip: string): Promise<boolean>;

  // Security Logs
  getSecurityLog(id: number): Promise<SecurityLog | undefined>;
  getSecurityLogsByType(eventType: string): Promise<SecurityLog[]>;
  getRecentSecurityLogs(limit?: number): Promise<SecurityLog[]>;
  getLoginHistory(limit?: number): Promise<SecurityLog[]>;
  createSecurityLog(log: InsertSecurityLog): Promise<SecurityLog>;
  getSecurityStats(): Promise<{
    totalBlocked: number;
    totalAllowed: number;
    byEventType: { type: string; count: number }[];
  }>;

  // FAQs
  getFAQ(id: number): Promise<FAQ | undefined>;
  getAllFAQs(): Promise<FAQ[]>;
  getVisibleFAQs(): Promise<FAQ[]>;
  createFAQ(faq: InsertFAQ): Promise<FAQ>;
  updateFAQ(id: number, faq: Partial<InsertFAQ>): Promise<FAQ | undefined>;
  deleteFAQ(id: number): Promise<boolean>;

  // WebAuthn Credentials
  createWebAuthnCredential(data: InsertWebAuthnCredential): Promise<WebAuthnCredential>;
  getWebAuthnCredentialsByUser(userId: number): Promise<WebAuthnCredential[]>;
  getWebAuthnCredentialById(credentialId: string): Promise<WebAuthnCredential | undefined>;
  updateWebAuthnCredentialCounter(id: number, counter: number): Promise<void>;
  deleteWebAuthnCredential(id: number): Promise<boolean>;

  // 2FA
  enable2FA(userId: number, secret: string): Promise<User | undefined>;
  disable2FA(userId: number): Promise<User | undefined>;
  verify2FA(userId: number, token: string): Promise<boolean>;
  enableBiometricLogin(userId: number, credentialId: string): Promise<User | undefined>;
  disableBiometricLogin(userId: number): Promise<User | undefined>;
}

export class DatabaseStorage implements IStorage {
  private db = db; // Alias db to this.db for consistency

  // Users
  async getUser(id: number): Promise<User | undefined> {
    const [user] = await this.db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await this.db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await this.db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async getUserByUsernameOrEmail(credential: string): Promise<User | undefined> {
    const [user] = await this.db.select().from(users).where(
      sql`${users.username} = ${credential} OR ${users.email} = ${credential}`
    );
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await this.db.insert(users).values(insertUser).returning();
    return user;
  }

  async updateUser(id: number, userData: Partial<InsertUser>): Promise<User | undefined> {
    const [user] = await this.db.update(users).set(userData).where(eq(users.id, id)).returning();
    return user || undefined;
  }

  async deleteUser(id: number): Promise<boolean> {
    const result = await this.db.delete(users).where(eq(users.id, id)).returning();
    return result.length > 0;
  }

  async getAllUsers(): Promise<User[]> {
    return await this.db.select().from(users).orderBy(desc(users.createdAt));
  }

  // Projects
  async getProject(id: number): Promise<Project | undefined> {
    const [project] = await this.db.select().from(projects).where(eq(projects.id, id));
    return project || undefined;
  }

  async getAllProjects(): Promise<Project[]> {
    return await this.db.select().from(projects).orderBy(desc(projects.createdAt));
  }

  async getPublishedProjects(): Promise<Project[]> {
    return await this.db.select().from(projects).where(eq(projects.status, "Published")).orderBy(desc(projects.createdAt));
  }

  async createProject(insertProject: InsertProject): Promise<Project> {
    const [project] = await this.db.insert(projects).values({
      ...insertProject,
      tech: insertProject.tech ? [...insertProject.tech] : []
    }).returning();
    return project;
  }

  async updateProject(id: number, projectData: Partial<InsertProject>): Promise<Project | undefined> {
    const updateData: any = { ...projectData, updatedAt: new Date() };
    if (projectData.tech) {
      updateData.tech = [...projectData.tech];
    }
    const [project] = await this.db.update(projects).set(updateData).where(eq(projects.id, id)).returning();
    return project || undefined;
  }

  async deleteProject(id: number): Promise<boolean> {
    const result = await this.db.delete(projects).where(eq(projects.id, id)).returning();
    return result.length > 0;
  }

  async incrementProjectViews(id: number): Promise<void> {
    await this.db.update(projects).set({ views: sql`${projects.views} + 1` }).where(eq(projects.id, id));
  }

  // Posts
  async getPost(id: number): Promise<Post | undefined> {
    const [post] = await this.db.select().from(posts).where(eq(posts.id, id));
    return post || undefined;
  }

  async getPostBySlug(slug: string): Promise<Post | undefined> {
    const [post] = await this.db.select().from(posts).where(eq(posts.slug, slug));
    return post || undefined;
  }

  async getAllPosts(): Promise<Post[]> {
    return await this.db.select().from(posts).orderBy(desc(posts.createdAt));
  }

  async getPublishedPosts(): Promise<Post[]> {
    return await this.db.select().from(posts).where(eq(posts.status, "Published")).orderBy(desc(posts.publishedAt));
  }

  async createPost(insertPost: InsertPost): Promise<Post> {
    let publishedAt = null;
    if (insertPost.publishedAt) {
      if (typeof insertPost.publishedAt === 'string') {
        const date = new Date(insertPost.publishedAt);
        publishedAt = isNaN(date.getTime()) ? null : date;
      } else if (insertPost.publishedAt instanceof Date) {
        publishedAt = insertPost.publishedAt;
      }
    }
    const [post] = await this.db.insert(posts).values({
      ...insertPost,
      tags: insertPost.tags ? [...insertPost.tags] : [],
      publishedAt
    }).returning();
    return post;
  }

  async updatePost(id: number, postData: Partial<InsertPost>): Promise<Post | undefined> {
    const updateData: any = { ...postData, updatedAt: new Date() };
    if (postData.tags) {
      updateData.tags = [...postData.tags];
    }
    if ('publishedAt' in updateData) {
      if (updateData.publishedAt === null || updateData.publishedAt === undefined) {
        updateData.publishedAt = null;
      } else if (typeof updateData.publishedAt === 'string') {
        const date = new Date(updateData.publishedAt);
        updateData.publishedAt = isNaN(date.getTime()) ? null : date;
      } else if (!(updateData.publishedAt instanceof Date)) {
        updateData.publishedAt = null;
      }
    }
    const [post] = await this.db.update(posts).set(updateData).where(eq(posts.id, id)).returning();
    return post || undefined;
  }

  async deletePost(id: number): Promise<boolean> {
    const result = await this.db.delete(posts).where(eq(posts.id, id)).returning();
    return result.length > 0;
  }

  async incrementPostViews(id: number): Promise<void> {
    await this.db.update(posts).set({ views: sql`${posts.views} + 1` }).where(eq(posts.id, id));
  }

  // Pages
  async getPage(id: number): Promise<Page | undefined> {
    const [page] = await this.db.select().from(pages).where(eq(pages.id, id));
    return page || undefined;
  }

  async getPageBySlug(slug: string): Promise<Page | undefined> {
    const [page] = await this.db.select().from(pages).where(eq(pages.slug, slug));
    return page || undefined;
  }

  async getAllPages(): Promise<Page[]> {
    return await this.db.select().from(pages).orderBy(desc(pages.createdAt));
  }

  async getPublishedPages(): Promise<Page[]> {
    return await this.db.select().from(pages).where(eq(pages.status, "Published")).orderBy(desc(pages.publishedAt));
  }

  async createPage(insertPage: InsertPage): Promise<Page> {
    let publishedAt = null;
    if (insertPage.publishedAt) {
      if (typeof insertPage.publishedAt === 'string') {
        const date = new Date(insertPage.publishedAt);
        publishedAt = isNaN(date.getTime()) ? null : date;
      } else if (insertPage.publishedAt instanceof Date) {
        publishedAt = insertPage.publishedAt;
      }
    }
    const [page] = await this.db.insert(pages).values({
      ...insertPage,
      publishedAt
    }).returning();
    return page;
  }

  async updatePage(id: number, pageData: Partial<InsertPage>): Promise<Page | undefined> {
    const updateData: any = { ...pageData, updatedAt: new Date() };
    if ('publishedAt' in updateData) {
      if (updateData.publishedAt === null || updateData.publishedAt === undefined) {
        updateData.publishedAt = null;
      } else if (typeof updateData.publishedAt === 'string') {
        const date = new Date(updateData.publishedAt);
        updateData.publishedAt = isNaN(date.getTime()) ? null : date;
      } else if (!(updateData.publishedAt instanceof Date)) {
        updateData.publishedAt = null;
      }
    }
    const [page] = await this.db.update(pages).set(updateData).where(eq(pages.id, id)).returning();
    return page || undefined;
  }

  async deletePage(id: number): Promise<boolean> {
    const result = await this.db.delete(pages).where(eq(pages.id, id)).returning();
    return result.length > 0;
  }

  async incrementPageViews(id: number): Promise<void> {
    await this.db.update(pages).set({ views: sql`${pages.views} + 1` }).where(eq(pages.id, id));
  }

  // Skills
  async getSkill(id: number): Promise<Skill | undefined> {
    const [skill] = await this.db.select().from(skills).where(eq(skills.id, id));
    return skill || undefined;
  }

  async getAllSkills(): Promise<Skill[]> {
    return await this.db.select().from(skills).orderBy(asc(skills.order));
  }

  async createSkill(insertSkill: InsertSkill): Promise<Skill> {
    const [skill] = await this.db.insert(skills).values(insertSkill).returning();
    return skill;
  }

  async updateSkill(id: number, skillData: Partial<InsertSkill>): Promise<Skill | undefined> {
    const [skill] = await this.db.update(skills).set(skillData).where(eq(skills.id, id)).returning();
    return skill || undefined;
  }

  async deleteSkill(id: number): Promise<boolean> {
    const result = await this.db.delete(skills).where(eq(skills.id, id)).returning();
    return result.length > 0;
  }

  // Services
  async getService(id: number): Promise<Service | undefined> {
    const [service] = await this.db.select().from(services).where(eq(services.id, id));
    return service || undefined;
  }

  async getAllServices(): Promise<Service[]> {
    return await this.db.select().from(services).orderBy(asc(services.order));
  }

  async getActiveServices(): Promise<Service[]> {
    return await this.db.select().from(services).where(eq(services.active, true)).orderBy(asc(services.order));
  }

  async createService(insertService: InsertService): Promise<Service> {
    const [service] = await this.db.insert(services).values({
      ...insertService,
      features: insertService.features ? [...insertService.features] : []
    }).returning();
    return service;
  }

  async updateService(id: number, serviceData: Partial<InsertService>): Promise<Service | undefined> {
    const updateData: any = { ...serviceData };
    if (serviceData.features) {
      updateData.features = [...serviceData.features];
    }
    const [service] = await this.db.update(services).set(updateData).where(eq(services.id, id)).returning();
    return service || undefined;
  }

  async deleteService(id: number): Promise<boolean> {
    const result = await this.db.delete(services).where(eq(services.id, id)).returning();
    return result.length > 0;
  }

  // Testimonials
  async getTestimonial(id: number): Promise<Testimonial | undefined> {
    const [testimonial] = await this.db.select().from(testimonials).where(eq(testimonials.id, id));
    return testimonial || undefined;
