    blockRequests?: boolean;
  };
  sessionSettings?: {
    idleTimeout?: string;
    maxDuration?: string;
    forceLogoutInactive?: boolean;
    concurrentSessionLimit?: string;
  };
  deviceFingerprinting?: {
    enabled?: boolean;
    alertOnNewDevice?: boolean;
    blockSuspiciousDevices?: boolean;
  };
  compliance?: {
    gdprEnabled?: boolean;
    dataRetentionDays?: number;
    privacyPolicyUrl?: string;
    cookieConsentEnabled?: boolean;
  };
  apiSecurity?: {
    apiKeyRotationDays?: number;
    oauthEnabled?: boolean;
    webhooksEnabled?: boolean;
    rateLimitPerMin?: number;
  };
  cspSettings?: {
    enabled?: boolean;
    strictMode?: boolean;
    reportUri?: string;
  };
  scannerSettings?: {
    vulnerabilityScanEnabled?: boolean;
    sslScanEnabled?: boolean;
    malwareScanEnabled?: boolean;
    scanFrequency?: string;
  };
}

interface TrustedDevice {
  id: number;
  userId: number;
  deviceName: string;
  deviceFingerprint?: string;
  ipAddress?: string;
  userAgent?: string;
  trusted: boolean;
  lastUsed?: string;
  createdAt?: string;
}

interface UserSession {
  id: number;
  sessionId: string;
  userId: number;
  ipAddress?: string;
  userAgent?: string;
  deviceInfo?: string;
  location?: string;
  active: boolean;
  createdAt?: string;
  expiresAt?: string;
  lastActivity?: string;
}

interface IpRule {
  id: number;
  ipAddress: string;
  type: 'whitelist' | 'blacklist';
  reason?: string;
  createdBy?: number;
  createdAt?: string;
}

interface SecurityStats {
  totalBlocked: number;
  totalAllowed: number;
  byEventType: { type: string; count: number }[];
}

interface SecurityLog {
  id: number;
  action: string;
  userId?: number;
  userName?: string;
  type: string;
  createdAt?: string;
}

export default function AdminSecurityEnhanced() {
  const { toast } = useToast();
  const [newWhitelistIp, setNewWhitelistIp] = useState("");
  const [newBlacklistIp, setNewBlacklistIp] = useState("");
  const [showGoogleSecret, setShowGoogleSecret] = useState(false);
  const [showCloudflareSecret, setShowCloudflareSecret] = useState(false);
  const [showLoginHistory, setShowLoginHistory] = useState(false);

  // 2FA Modal
  const [show2FASetup, setShow2FASetup] = useState(false);
  const [show2FAVerify, setShow2FAVerify] = useState(false);
  const [show2FADisable, setShow2FADisable] = useState(false);
  const [qrCode, setQrCode] = useState("");
  const [secret, setSecret] = useState("");
  const [verifyCode, setVerifyCode] = useState("");
  const [disableCode, setDisableCode] = useState("");
  const [secretCopied, setSecretCopied] = useState(false);

  // Biometric Modal
  const [showBiometricSetup, setShowBiometricSetup] = useState(false);
  const [biometricDeviceName, setBiometricDeviceName] = useState("");
  const [isBiometricRegistering, setIsBiometricRegistering] = useState(false);

  const { data: settings = {}, isLoading: settingsLoading, refetch: refetchSettings } = useQuery<SecuritySettings>({
    queryKey: ['/api/security/settings'],
  });

  const { data: devices = [], refetch: refetchDevices } = useQuery<TrustedDevice[]>({
    queryKey: ['/api/security/devices'],
  });

  const { data: sessions = [], refetch: refetchSessions } = useQuery<UserSession[]>({
    queryKey: ['/api/security/sessions'],
  });

  const { data: ipRules = [], refetch: refetchIpRules } = useQuery<IpRule[]>({
    queryKey: ['/api/security/ip-rules'],
  });

  const { data: stats } = useQuery<SecurityStats>({
    queryKey: ['/api/security/stats'],
  });

  const { data: loginHistory = [], refetch: refetchLoginHistory } = useQuery<SecurityLog[]>({
    queryKey: ['/api/security/login-history'],
  });

  const { data: webauthnCredentials = [], refetch: refetchWebauthnCredentials } = useQuery<any[]>({
    queryKey: ['/api/auth/webauthn/credentials'],
  });

  const { data: user = null, refetch: refetchUser } = useQuery<any>({
    queryKey: ['/api/auth/me'],
  });

  const [localSettings, setLocalSettings] = useState<SecuritySettings>({});

  useEffect(() => {
    if (settings && Object.keys(settings).length > 0) {
      setLocalSettings(settings);
    }
  }, [settings]);

  const updateSettingsMutation = useMutation({
    mutationFn: async (newSettings: SecuritySettings) => {
      return apiRequest('POST', '/api/security/settings/bulk', newSettings);
    },
    onSuccess: () => {
      toast({ title: "Settings saved successfully" });
      refetchSettings();
    },
    onError: () => {
      toast({ title: "Failed to save settings", variant: "destructive" });
    }
  });

  const deleteDeviceMutation = useMutation({
    mutationFn: async (id: number) => {
      return apiRequest('DELETE', `/api/security/devices/${id}`);
    },
    onSuccess: () => {
      toast({ title: "Device removed" });
      refetchDevices();
    }
  });

  const terminateSessionMutation = useMutation({
    mutationFn: async (id: number) => {
      return apiRequest('POST', `/api/security/sessions/terminate/${id}`);
    },
    onSuccess: () => {
      toast({ title: "Session terminated" });
      refetchSessions();
    }
  });

  const terminateAllSessionsMutation = useMutation({
    mutationFn: async () => {
      return apiRequest('POST', '/api/security/sessions/terminate-all');
    },
    onSuccess: () => {
      toast({ title: "All sessions terminated" });
      refetchSessions();
    }
  });

  const logoutAllDevicesMutation = useMutation({
    mutationFn: async () => {
      return apiRequest('POST', '/api/security/sessions/logout-all-devices');
    },
    onSuccess: () => {
      toast({ title: "Logged out from all devices" });
      refetchSessions();
    }
  });

  const addIpRuleMutation = useMutation({
    mutationFn: async ({ ipAddress, type }: { ipAddress: string; type: 'whitelist' | 'blacklist' }) => {
      return apiRequest('POST', '/api/security/ip-rules', { ipAddress, type });
    },
    onSuccess: () => {
      toast({ title: "IP rule added" });
      setNewWhitelistIp("");
      setNewBlacklistIp("");
      refetchIpRules();
    }
  });

  const deleteIpRuleMutation = useMutation({
    mutationFn: async (id: number) => {
      return apiRequest('DELETE', `/api/security/ip-rules/${id}`);
    },
    onSuccess: () => {
      toast({ title: "IP rule removed" });
      refetchIpRules();
    }
  });

  // 2FA Mutations
  const generate2FAMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest('POST', '/api/auth/2fa/generate');
      return await res.json();
    },
    onSuccess: (data: any) => {
      if (data?.secret && data?.qrCode) {
        setSecret(data.secret);
        setQrCode(data.qrCode);
        setShow2FASetup(true);
      } else {
        toast({ title: "Failed to generate 2FA setup", description: "Missing secret or QR code", variant: "destructive" });
      }
    },
    onError: (error: any) => {
      toast({ title: "Failed to generate 2FA setup", description: error.message || "Unknown error", variant: "destructive" });
    }
  });

  const verify2FAMutation = useMutation({
    mutationFn: async (token: string) => {
      const res = await apiRequest('POST', '/api/auth/2fa/verify', { token });
      return await res.json();
    },
    onSuccess: () => {
      toast({ title: "2FA enabled successfully" });
      setShow2FASetup(false);
      setShow2FAVerify(false);
      setVerifyCode("");
      refetchUser();
      refetchSettings();
    },
    onError: () => {
      toast({ title: "Invalid verification code", variant: "destructive" });
    }
  });

  const disable2FAMutation = useMutation({
    mutationFn: async (token: string) => {
      const res = await apiRequest('POST', '/api/auth/2fa/disable', { token });
      return await res.json();
    },
    onSuccess: () => {
      toast({ title: "2FA disabled successfully" });
      setShow2FADisable(false);
      setDisableCode("");
      refetchUser();
      refetchSettings();
    },
    onError: () => {
      toast({ title: "Invalid verification code", variant: "destructive" });
    }
  });

  // Biometric Mutations
  const registerBiometricMutation = useMutation({
    mutationFn: async () => {
      setIsBiometricRegistering(true);
      try {
        // Get registration options
        const optionsRes = await apiRequest('GET', '/api/auth/webauthn/register/options');

        // Start WebAuthn registration
        const credential = await (navigator as any).credentials.create({
          publicKey: optionsRes
        });

        // Verify registration
        await apiRequest('POST', '/api/auth/webauthn/register/verify', {
          credential: {
            id: credential.id,
            rawId: btoa(String.fromCharCode.apply(null, Array.from(new Uint8Array(credential.rawId)))),
            response: {
              clientDataJSON: btoa(String.fromCharCode.apply(null, Array.from(new Uint8Array(credential.response.clientDataJSON)))),
              attestationObject: btoa(String.fromCharCode.apply(null, Array.from(new Uint8Array(credential.response.attestationObject))))
            },
            type: credential.type
          },
          deviceName: biometricDeviceName || 'Biometric Device'
        });

        return credential;
      } finally {
        setIsBiometricRegistering(false);
      }
    },
    onSuccess: () => {
      toast({ title: "Biometric device registered successfully" });
      setShowBiometricSetup(false);
      setBiometricDeviceName("");
      refetchWebauthnCredentials();
      refetchSettings();
    },
    onError: (error: any) => {
      toast({ 
        title: "Biometric registration failed", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  const deleteWebauthnCredentialMutation = useMutation({
    mutationFn: async (id: number) => {
      const res = await apiRequest('DELETE', `/api/auth/webauthn/credentials/${id}`);
      return await res.json();
    },
    onSuccess: () => {
      toast({ title: "Biometric device removed" });
      refetchWebauthnCredentials();
    }
  });

  const updateLocalSetting = (key: string, value: any) => {
    setLocalSettings(prev => ({ ...prev, [key]: value }));
  };

  const updateNestedSetting = (parentKey: string, childKey: string, value: any) => {
    setLocalSettings(prev => ({
      ...prev,
      [parentKey]: {
        ...(prev[parentKey as keyof SecuritySettings] as Record<string, any> || {}),
        [childKey]: value
      }
    }));
  };

  const saveAllSettings = () => {
    updateSettingsMutation.mutate(localSettings);
  };

  const handle2FAToggle = (enabled: boolean) => {
    if (enabled) {
      generate2FAMutation.mutate();
    } else {
