                <div>
                  <label className="text-sm font-medium">Scan Frequency</label>
                  <select 
                    className="w-full p-2 rounded-md border border-input bg-background mt-1"
                    value={localSettings.scannerSettings?.scanFrequency || 'weekly'}
                    onChange={(e) => updateNestedSetting('scannerSettings', 'scanFrequency', e.target.value)}
                    data-testid="select-scan-frequency"
                  >
                    <option value="daily">Daily</option>
                    <option value="weekly">Weekly</option>
                    <option value="monthly">Monthly</option>
                  </select>
                </div>
                <Button onClick={saveAllSettings} disabled={updateSettingsMutation.isPending} className="w-full" data-testid="button-save-scanner">
                  {updateSettingsMutation.isPending && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
                  Save Scanner Settings
                </Button>
              </CardContent>
            </Card>
          </TabsContent>

          {/* ACTIVITY TAB */}
          <TabsContent value="activity" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Activity className="w-5 h-5" /> Security Activity
                </CardTitle>
                <CardDescription>Monitor user actions and security events</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  <div className="p-3 border rounded-lg bg-blue-500/5">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="font-medium text-sm">Last Login Activity</p>
                        <p className="text-xs text-muted-foreground">Security events from last 24 hours</p>
                      </div>
                      <Badge variant="secondary">Active</Badge>
                    </div>
                  </div>
                  <div className="p-3 border rounded-lg">
                    <div className="flex items-center justify-between mb-2">
                      <p className="text-sm font-medium">Login Attempts</p>
                      <span className="text-lg font-bold">{stats?.byEventType?.find(e => e.type === 'login')?.count || 0}</span>
                    </div>
                    <div className="h-2 bg-muted rounded-full overflow-hidden">
                      <div className="h-full bg-green-500 rounded-full" style={{ width: '60%' }} />
                    </div>
                  </div>
                  <div className="p-3 border rounded-lg">
                    <div className="flex items-center justify-between mb-2">
                      <p className="text-sm font-medium">Failed Login Attempts</p>
                      <span className="text-lg font-bold">{stats?.byEventType?.find(e => e.type === 'failed_login')?.count || 0}</span>
                    </div>
                    <div className="h-2 bg-muted rounded-full overflow-hidden">
                      <div className="h-full bg-red-500 rounded-full" style={{ width: '15%' }} />
                    </div>
                  </div>
                  <div className="p-3 border rounded-lg">
                    <div className="flex items-center justify-between mb-2">
                      <p className="text-sm font-medium">Settings Changes</p>
                      <span className="text-lg font-bold">{stats?.byEventType?.find(e => e.type === 'settings_change')?.count || 0}</span>
                    </div>
                    <div className="h-2 bg-muted rounded-full overflow-hidden">
                      <div className="h-full bg-blue-500 rounded-full" style={{ width: '40%' }} />
                    </div>
                  </div>
                </div>
                <Button 
                  onClick={() => setShowLoginHistory(!showLoginHistory)} 
                  className="w-full" 
                  data-testid="button-view-login-history"
                >
                  <FileText className="w-4 h-4 mr-2" /> {showLoginHistory ? 'Hide' : 'View'} Login History
                </Button>
                {showLoginHistory && (
                  <div className="mt-4 space-y-2 max-h-96 overflow-y-auto">
                    <p className="text-sm font-medium">Recent Login Activity</p>
                    {loginHistory.length === 0 ? (
                      <p className="text-xs text-muted-foreground">No login history found</p>
                    ) : (
                      loginHistory.map((log) => (
                        <div key={log.id} className="p-2 text-xs border rounded bg-muted/50">
                          <div className="flex items-center justify-between">
                            <span className="font-medium">{log.userName || 'Unknown User'}</span>
                            <span className="text-muted-foreground">{log.createdAt ? new Date(log.createdAt).toLocaleString() : 'N/A'}</span>
                          </div>
                          <p className="text-muted-foreground">{log.action}</p>
                        </div>
                      ))
                    )}
                  </div>
                )}
                <Button 
                  onClick={() => {
                    refetchLoginHistory();
                    refetchSessions();
                  }} 
                  disabled={false} 
                  className="w-full" 
                  data-testid="button-refresh-activity"
                >
                  <RefreshCw className="w-4 h-4 mr-2" /> Refresh Activity Log
                </Button>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>

      {/* 2FA Setup Modal */}
      <Dialog open={show2FASetup} onOpenChange={setShow2FASetup}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Set up Two-Factor Authentication</DialogTitle>
            <DialogDescription>
              Scan this QR code with your authenticator app (Google Authenticator, Authy, etc.)
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            {qrCode && (
              <div className="flex justify-center p-4 bg-white rounded-lg">
                <img src={qrCode} alt="2FA QR Code" className="w-48 h-48" />
              </div>
            )}
            <div className="space-y-2">
              <Label className="text-sm font-medium">Or enter this code manually:</Label>
              <div className="flex gap-2">
                <Input value={secret} readOnly className="font-mono text-sm" />
                <Button 
                  variant="outline" 
                  size="icon"
                  onClick={copySecret}
                >
                  {secretCopied ? <Check className="w-4 h-4" /> : <Copy className="w-4 h-4" />}
                </Button>
              </div>
            </div>
          </div>
          <DialogFooter>
            <Button 
              onClick={() => {
                setShow2FASetup(false);
                setShow2FAVerify(true);
              }}
            >
              Continue to Verification
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* 2FA Verify Modal */}
      <Dialog open={show2FAVerify} onOpenChange={setShow2FAVerify}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Verify 2FA Setup</DialogTitle>
            <DialogDescription>
              Enter the 6-digit code from your authenticator app
            </DialogDescription>
          </DialogHeader>
          <div className="flex justify-center py-6">
            <InputOTP maxLength={6} value={verifyCode} onChange={setVerifyCode}>
              <InputOTPGroup>
                <InputOTPSlot index={0} />
                <InputOTPSlot index={1} />
                <InputOTPSlot index={2} />
                <InputOTPSlot index={3} />
                <InputOTPSlot index={4} />
                <InputOTPSlot index={5} />
              </InputOTPGroup>
            </InputOTP>
          </div>
          <DialogFooter className="gap-2">
            <Button 
              variant="outline" 
              onClick={() => {
                setShow2FAVerify(false);
                setVerifyCode("");
              }}
            >
              Cancel
            </Button>
            <Button 
              onClick={() => verify2FAMutation.mutate(verifyCode)}
              disabled={verifyCode.length !== 6 || verify2FAMutation.isPending}
            >
              {verify2FAMutation.isPending && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
              Verify & Enable
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* 2FA Disable Modal */}
      <Dialog open={show2FADisable} onOpenChange={setShow2FADisable}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Disable Two-Factor Authentication</DialogTitle>
            <DialogDescription>
              Enter your 6-digit code to confirm disabling 2FA
            </DialogDescription>
          </DialogHeader>
          <div className="flex justify-center py-6">
            <InputOTP maxLength={6} value={disableCode} onChange={setDisableCode}>
              <InputOTPGroup>
                <InputOTPSlot index={0} />
                <InputOTPSlot index={1} />
                <InputOTPSlot index={2} />
                <InputOTPSlot index={3} />
                <InputOTPSlot index={4} />
                <InputOTPSlot index={5} />
              </InputOTPGroup>
            </InputOTP>
          </div>
          <DialogFooter className="gap-2">
            <Button 
              variant="outline" 
              onClick={() => {
                setShow2FADisable(false);
                setDisableCode("");
              }}
            >
              Cancel
            </Button>
            <Button 
              variant="destructive"
              onClick={() => disable2FAMutation.mutate(disableCode)}
              disabled={disableCode.length !== 6 || disable2FAMutation.isPending}
            >
              {disable2FAMutation.isPending && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
              Disable 2FA
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Biometric Setup Modal */}
      <Dialog open={showBiometricSetup} onOpenChange={setShowBiometricSetup}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Add Biometric Device</DialogTitle>
            <DialogDescription>
              Register your fingerprint or face for quick login
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label>Device Name (Optional)</Label>
              <Input 
                placeholder="e.g., My iPhone, Work Laptop"
                value={biometricDeviceName}
                onChange={(e) => setBiometricDeviceName(e.target.value)}
              />
            </div>
            <div className="p-4 bg-muted/50 rounded-lg">
              <p className="text-sm text-muted-foreground">
                <Fingerprint className="w-4 h-4 inline mr-2" />
                You'll be prompted to use your device's biometric sensor
              </p>
            </div>
          </div>
          <DialogFooter className="gap-2">
            <Button 
              variant="outline" 
              onClick={() => {
                setShowBiometricSetup(false);
                setBiometricDeviceName("");
              }}
            >
              Cancel
            </Button>
            <Button 
              onClick={() => registerBiometricMutation.mutate()}
              disabled={isBiometricRegistering}
            >
              {isBiometricRegistering && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
              Register Device
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </AdminLayout>
  );
}

