    staleTime: 1000 * 60 * 5,
  });

  const footerText = settings?.footerText || "Crafted with love & countless cups of coffee";
  const footerCopyright = settings?.footerCopyright || "2024 Loi Developer. All rights reserved.";
  const socialGithub = settings?.socialGithub || "";
  const socialLinkedin = settings?.socialLinkedin || "";
  const socialTwitter = settings?.socialTwitter || "";

  useEffect(() => {
    const handleScroll = () => {
      setShowScrollTop(window.scrollY > 500);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const socialLinks = [
    { url: socialGithub, icon: Github, name: "GitHub" },
    { url: socialLinkedin, icon: Linkedin, name: "LinkedIn" },
    { url: socialTwitter, icon: Twitter, name: "Twitter" },
  ].filter(s => s.url);

  return (
    <footer className="py-8 bg-background border-t border-border/50 relative overflow-hidden">
      <div className="container mx-auto px-4 flex flex-col md:flex-row items-center justify-between gap-4">
        <div className="text-sm text-muted-foreground flex flex-col md:flex-row items-center gap-4">
          <span className="font-mono">{footerText}</span>
          <div className="mt-1 md:mt-0">{footerCopyright}</div>
        </div>

        <div className="flex items-center gap-4">
          <Link href="/admin/login" className="text-muted-foreground hover:text-primary transition-colors" title="Admin Login">
             <LayoutDashboard size={18} />
          </Link>
          {socialLinks.length > 0 && (
            <>
              <div className="h-4 w-px bg-border mx-2 hidden md:block" />
              {socialLinks.map((social, i) => (
                <a
                  key={i}
                  href={social.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="p-2 text-muted-foreground hover:text-primary transition-colors hover:scale-110 transform duration-300"
                  data-testid={`link-footer-${social.name.toLowerCase()}`}
                >
                  <social.icon size={20} />
                </a>
              ))}
            </>
          )}
        </div>
      </div>

      {showScrollTop && (
        <button
          onClick={scrollToTop}
          className="fixed bottom-8 right-8 p-3 bg-primary text-white rounded-full shadow-lg hover:bg-primary/80 transition-all duration-300 z-40 animate-in fade-in zoom-in"
          data-testid="button-scroll-top"
        >
          <ArrowUp size={24} />
        </button>
      )}
    </footer>
  );
}

client/src/pages/admin/System.tsx.content:
import { AdminLayout } from "@/layouts/AdminLayout";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Database, 
  RotateCcw, 
  Clock, 
  ShieldCheck,
  AlertTriangle,
  HardDrive,
  RefreshCw,
  Trash2,
  Activity,
  Users,
  FileText,
  Briefcase,
  MessageSquare,
  Loader2,
  CheckCircle,
  XCircle,
  Info
} from "lucide-react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import { formatDistanceToNow } from "date-fns";

function formatUptime(seconds: number): string {
  const days = Math.floor(seconds / 86400);
  const hours = Math.floor((seconds % 86400) / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);

  if (days > 0) {
    return `${days}d ${hours}h ${minutes}m`;
  } else if (hours > 0) {
    return `${hours}h ${minutes}m`;
  } else {
    return `${minutes}m`;
  }
}

function getIconForType(type: string) {
  switch (type) {
    case "success": return <CheckCircle className="w-4 h-4 text-green-500" />;
    case "error": return <XCircle className="w-4 h-4 text-red-500" />;
    case "warning": return <AlertTriangle className="w-4 h-4 text-yellow-500" />;
    default: return <Info className="w-4 h-4 text-blue-500" />;
  }
}

export default function AdminSystem() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: systemStats, isLoading: statsLoading, refetch: refetchStats } = useQuery({
    queryKey: ["/api/system/stats"],
    queryFn: () => api.getSystemStats(),
    refetchInterval: 30000,
  });

  const { data: activityLogs = [], isLoading: logsLoading, refetch: refetchLogs } = useQuery({
    queryKey: ["/api/system/activity-logs"],
    queryFn: () => api.getSystemActivityLogs(100, 0),
  });

  const clearLogsMutation = useMutation({
    mutationFn: () => api.clearActivityLogs(),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/system/activity-logs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/system/stats"] });
      refetchStats();
      refetchLogs();
      toast({ title: "Success", description: "Activity logs cleared" });
    },
    onError: (error: any) => {
      toast({ title: "Error", description: error.message || "Failed to clear logs", variant: "destructive" });
    },
  });

  const resetMutation = useMutation({
    mutationFn: () => api.resetSystem(),
    onSuccess: (data) => {
      refetchStats();
      refetchLogs();
      toast({ title: "Info", description: data.message });
    },
    onError: (error: any) => {
      toast({ title: "Error", description: error.message || "Failed to reset system", variant: "destructive" });
    },
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case "Healthy": return "text-green-500";
      case "Warning": return "text-yellow-500";
      case "Error": return "text-red-500";
      default: return "text-muted-foreground";
    }
  };

  const getStatusBgColor = (status: string) => {
    switch (status) {
      case "Healthy": return "bg-green-500/5 border-green-500/20";
      case "Warning": return "bg-yellow-500/5 border-yellow-500/20";
      case "Error": return "bg-red-500/5 border-red-500/20";
      default: return "";
    }
  };

  return (
    <AdminLayout>
      <div className="space-y-8">
        <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
          <div>
            <h1 className="text-3xl font-heading font-bold">System & Monitoring</h1>
            <p className="text-muted-foreground">Monitor system health and activity logs</p>
          </div>
          <div className="flex gap-2">
            <Button 
              variant="outline" 
              onClick={() => refetchStats()}
              disabled={statsLoading}
              data-testid="button-refresh-stats"
            >
              <RefreshCw className={`w-4 h-4 mr-2 ${statsLoading ? 'animate-spin' : ''}`} />
              Refresh
            </Button>
            <AlertDialog>
              <AlertDialogTrigger asChild>
                <Button 
                  variant="outline" 
                  className="text-destructive border-destructive/50"
                  data-testid="button-reset-system"
                >
                  <RotateCcw className="w-4 h-4 mr-2" /> Reset System
                </Button>
              </AlertDialogTrigger>
              <AlertDialogContent>
                <AlertDialogHeader>
                  <AlertDialogTitle>Reset System</AlertDialogTitle>
                  <AlertDialogDescription>
                    This action will attempt to reset the system. This operation is restricted in this environment.
                  </AlertDialogDescription>
                </AlertDialogHeader>
                <AlertDialogFooter>
                  <AlertDialogCancel>Cancel</AlertDialogCancel>
                  <AlertDialogAction 
                    onClick={() => resetMutation.mutate()}
                    className="bg-destructive text-destructive-foreground"
                  >
                    {resetMutation.isPending ? <Loader2 className="w-4 h-4 animate-spin" /> : "Reset"}
                  </AlertDialogAction>
                </AlertDialogFooter>
              </AlertDialogContent>
            </AlertDialog>
          </div>
        </div>

        {statsLoading ? (
          <div className="flex items-center justify-center py-12">
            <Loader2 className="w-8 h-8 animate-spin text-primary" />
          </div>
        ) : (
          <>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <Card className={getStatusBgColor(systemStats?.status || "Healthy")}>
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
                    <ShieldCheck className={`w-4 h-4 ${getStatusColor(systemStats?.status || "Healthy")}`} /> 
                    System Status
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className={`text-2xl font-bold ${getStatusColor(systemStats?.status || "Healthy")}`}>
                    {systemStats?.status || "Checking..."}
                  </div>
                  <p className="text-xs text-muted-foreground mt-1">
                    Last check: {systemStats?.lastCheck ? formatDistanceToNow(new Date(systemStats.lastCheck), { addSuffix: true }) : "Unknown"}
                  </p>
                </CardContent>
              </Card>

              <Card>
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
                    <HardDrive className="w-4 h-4 text-primary" /> Database Size
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold">{systemStats?.databaseSize || "Unknown"}</div>
                  <p className="text-xs text-muted-foreground mt-1">
                    {systemStats?.tableStats?.reduce((sum, t) => sum + t.count, 0) || 0} total records
                  </p>
                </CardContent>
              </Card>

              <Card>
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
                    <Clock className="w-4 h-4 text-blue-500" /> Server Uptime
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold">
                    {systemStats?.serverUptime ? formatUptime(systemStats.serverUptime) : "Unknown"}
                  </div>
                  <p className="text-xs text-muted-foreground mt-1">Since last restart</p>
                </CardContent>
              </Card>
            </div>

            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Database className="w-5 h-5" />
                  Database Tables
                </CardTitle>
                <CardDescription>Record counts per table</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-5 gap-4">
                  {systemStats?.tableStats?.map((table) => (
                    <div 
                      key={table.name} 
                      className="flex items-center gap-3 p-3 rounded-lg bg-muted/50"
                    >
                      <div className="p-2 rounded-md bg-primary/10">
                        {table.name === "Users" && <Users className="w-4 h-4 text-primary" />}
                        {table.name === "Projects" && <Briefcase className="w-4 h-4 text-primary" />}
                        {table.name === "Posts" && <FileText className="w-4 h-4 text-primary" />}
                        {table.name === "Messages" && <MessageSquare className="w-4 h-4 text-primary" />}
                        {!["Users", "Projects", "Posts", "Messages"].includes(table.name) && 
                          <Database className="w-4 h-4 text-primary" />}
                      </div>
                      <div>
                        <p className="font-semibold">{table.count}</p>
                        <p className="text-xs text-muted-foreground">{table.name}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </>
        )}

        <Card>
          <CardHeader>
            <div className="flex items-center justify-between gap-4">
              <div>
                <CardTitle className="flex items-center gap-2">
                  <Activity className="w-5 h-5" />
                  Activity Log
                </CardTitle>
                <CardDescription>Recent system and user activities</CardDescription>
              </div>
              <AlertDialog>
                <AlertDialogTrigger asChild>
                  <Button 
                    variant="outline" 
                    size="sm"
                    className="text-destructive"
                    data-testid="button-clear-logs"
                  >
                    <Trash2 className="w-4 h-4 mr-2" /> Clear Logs
                  </Button>
                </AlertDialogTrigger>
                <AlertDialogContent>
                  <AlertDialogHeader>
                    <AlertDialogTitle>Clear Activity Logs</AlertDialogTitle>
                    <AlertDialogDescription>
                      This will permanently delete all activity logs. This action cannot be undone.
                    </AlertDialogDescription>
                  </AlertDialogHeader>
                  <AlertDialogFooter>
                    <AlertDialogCancel>Cancel</AlertDialogCancel>
                    <AlertDialogAction 
                      onClick={() => clearLogsMutation.mutate()}
                      className="bg-destructive text-destructive-foreground"
                    >
                      {clearLogsMutation.isPending ? <Loader2 className="w-4 h-4 animate-spin" /> : "Clear All"}
                    </AlertDialogAction>
                  </AlertDialogFooter>
                </AlertDialogContent>
              </AlertDialog>
            </div>
          </CardHeader>
          <CardContent>
            {logsLoading ? (
              <div className="flex items-center justify-center py-8">
                <Loader2 className="w-6 h-6 animate-spin text-primary" />
              </div>
            ) : activityLogs && activityLogs.length > 0 ? (
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Action</TableHead>
                    <TableHead>User</TableHead>
                    <TableHead>Type</TableHead>
                    <TableHead className="text-right">Time</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {activityLogs.map((log: any) => (
                    <TableRow key={log.id}>
                      <TableCell>
                        <div className="flex items-center gap-2">
                          {getIconForType(log.type)}
                          <span className="font-medium">{log.action}</span>
                        </div>
                      </TableCell>
                      <TableCell className="text-muted-foreground">
                        {log.userName || "System"}
                      </TableCell>
                      <TableCell>
                        <Badge 
                          variant={
                            log.type === "success" ? "default" : 
                            log.type === "error" ? "destructive" : 
                            log.type === "warning" ? "secondary" : "outline"
                          }
                          className="text-xs"
                        >
                          {log.type}
                        </Badge>
                      </TableCell>
                      <TableCell className="text-right text-muted-foreground text-sm">
                        {log.createdAt ? formatDistanceToNow(new Date(log.createdAt), { addSuffix: true }) : "Unknown"}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            ) : (
              <div className="text-center py-8 text-muted-foreground">
                <Activity className="w-12 h-12 mx-auto mb-4 opacity-20" />
                <p>No activity logs found</p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </AdminLayout>
  );
}

client/src/index.css.content:
@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --color-background: hsl(var(--background));
  --color-foreground: hsl(var(--foreground));

  --color-card: hsl(var(--card));
  --color-card-foreground: hsl(var(--card-foreground));

  --color-popover: hsl(var(--popover));
  --color-popover-foreground: hsl(var(--popover-foreground));

  --color-primary: hsl(var(--primary));
  --color-primary-foreground: hsl(var(--primary-foreground));

  --color-secondary: hsl(var(--secondary));
  --color-secondary-foreground: hsl(var(--secondary-foreground));

  --color-muted: hsl(var(--muted));
  --color-muted-foreground: hsl(var(--muted-foreground));

  --color-accent: hsl(var(--accent));
  --color-accent-foreground: hsl(var(--accent-foreground));

  --color-destructive: hsl(var(--destructive));
  --color-destructive-foreground: hsl(var(--destructive-foreground));

  --color-border: hsl(var(--border));
  --color-input: hsl(var(--input));
  --color-ring: hsl(var(--ring));

  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);

  --font-sans: "Inter", sans-serif;
  --font-heading: "Outfit", sans-serif;
  --font-mono: "Space Grotesk", monospace;
}

/* Dark Mode by Default as requested for "Creative Coder" vibe */
:root {
  --background: 222 47% 11%; /* Slate 950 */
  --foreground: 210 40% 98%;

  --card: 222 47% 11%;
  --card-foreground: 210 40% 98%;

  --popover: 222 47% 11%;
  --popover-foreground: 210 40% 98%;

  --primary: 263 70% 50%; /* Purple Neon */
  --primary-foreground: 210 40% 98%;

  --secondary: 199 89% 48%; /* Cyan Neon */
  --secondary-foreground: 210 40% 98%;

  --muted: 217 33% 17%;
  --muted-foreground: 215 20% 65%;

  --accent: 217 33% 17%;
  --accent-foreground: 210 40% 98%;

  --destructive: 0 62% 30%;
  --destructive-foreground: 210 40% 98%;

  --border: 217 33% 17%;
  --input: 217 33% 17%;
  --ring: 263 70% 50%;

  --radius: 0.75rem;
}

.dark {
  /* Same as root since we are dark default, but explicit for toggling if needed */
  --background: 222 47% 11%;
  --foreground: 210 40% 98%;
  --card: 222 47% 11%;
  --card-foreground: 210 40% 98%;
  --popover: 222 47% 11%;
  --popover-foreground: 210 40% 98%;
  --primary: 263 70% 50%;
  --primary-foreground: 210 40% 98%;
  --secondary: 199 89% 48%;
  --secondary-foreground: 210 40% 98%;
  --muted: 217 33% 17%;
  --muted-foreground: 215 20% 65%;
  --accent: 217 33% 17%;
  --accent-foreground: 210 40% 98%;
  --destructive: 0 62% 30%;
  --destructive-foreground: 210 40% 98%;
  --border: 217 33% 17%;
  --input: 217 33% 17%;
  --ring: 263 70% 50%;
}

/* Light Mode - Clean & Sharp */
.light {
  --background: 0 0% 100%;
  --foreground: 222 47% 11%;

  --card: 0 0% 100%;
  --card-foreground: 222 47% 11%;

  --popover: 0 0% 100%;
  --popover-foreground: 222 47% 11%;

  --primary: 263 70% 50%;
  --primary-foreground: 210 40% 98%;

  --secondary: 199 89% 48%;
  --secondary-foreground: 210 40% 98%;

  --muted: 210 40% 96.1%;
  --muted-foreground: 215.4 16.3% 46.9%;

  --accent: 210 40% 96.1%;
  --accent-foreground: 222.2 47.4% 11.2%;

  --destructive: 0 84.2% 60.2%;
  --destructive-foreground: 210 40% 98%;

  --border: 214.3 31.8% 91.4%;
  --input: 214.3 31.8% 91.4%;
  --ring: 263 70% 50%;
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply font-sans antialiased bg-background text-foreground selection:bg-primary/30 selection:text-primary-foreground;
  }
  h1, h2, h3, h4, h5, h6 {
    @apply font-heading font-bold tracking-tight;
  }
}

@layer utilities {
  .glass {
    @apply bg-background/60 backdrop-blur-md border-b border-white/10;
  }
  .text-gradient {
    @apply bg-clip-text text-transparent bg-gradient-to-r from-primary via-purple-400 to-secondary;
  }
  .text-gradient-gold {
    @apply bg-clip-text text-transparent bg-gradient-to-r from-yellow-400 via-orange-300 to-yellow-600;
  }

  /* Custom Scrollbar */
  ::-webkit-scrollbar {
    width: 8px;
  }
  ::-webkit-scrollbar-track {
    @apply bg-muted;
  }
  ::-webkit-scrollbar-thumb {
    @apply bg-primary/50 rounded-full hover:bg-primary transition-colors;
  }
}

/* Hacker/IT Style Animations */

/* Glitch Text Effect */
.glitch-text {
  position: relative;
  animation: glitch-skew 1s infinite linear alternate-reverse;
}

.glitch-text::before,
.glitch-text::after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: hsl(var(--background));
}

.glitch-text::before {
  left: 2px;
  text-shadow: -2px 0 #00ff41;
  clip: rect(44px, 450px, 56px, 0);
  animation: glitch-anim 5s infinite linear alternate-reverse;
}

.glitch-text::after {
  left: -2px;
  text-shadow: -2px 0 #00d4ff, 2px 2px #7c3aed;
  animation: glitch-anim2 1s infinite linear alternate-reverse;
}

@keyframes glitch-anim {
  0% {
    clip: rect(10px, 9999px, 31px, 0);
    transform: skew(0.85deg);
  }
  5% {
    clip: rect(70px, 9999px, 71px, 0);
    transform: skew(0.34deg);
  }
  10% {
    clip: rect(35px, 9999px, 58px, 0);
    transform: skew(0.92deg);
  }
  15% {
    clip: rect(12px, 9999px, 83px, 0);
    transform: skew(0.08deg);
  }
  20% {
    clip: rect(45px, 9999px, 40px, 0);
    transform: skew(0.53deg);
  }
  25% {
    clip: rect(76px, 9999px, 23px, 0);
    transform: skew(0.17deg);
  }
  30% {
    clip: rect(67px, 9999px, 62px, 0);
    transform: skew(0.89deg);
  }
  35% {
    clip: rect(5px, 9999px, 90px, 0);
    transform: skew(0.42deg);
  }
  40% {
    clip: rect(88px, 9999px, 15px, 0);
    transform: skew(0.71deg);
  }
  45% {
    clip: rect(21px, 9999px, 49px, 0);
    transform: skew(0.25deg);
  }
  50% {
    clip: rect(33px, 9999px, 78px, 0);
    transform: skew(0.63deg);
  }
  55% {
    clip: rect(92px, 9999px, 11px, 0);
    transform: skew(0.14deg);
  }
  60% {
    clip: rect(48px, 9999px, 55px, 0);
    transform: skew(0.95deg);
  }
  65% {
    clip: rect(7px, 9999px, 87px, 0);
    transform: skew(0.38deg);
  }
  70% {
    clip: rect(59px, 9999px, 29px, 0);
    transform: skew(0.77deg);
  }
  75% {
    clip: rect(81px, 9999px, 44px, 0);
    transform: skew(0.02deg);
  }
  80% {
    clip: rect(16px, 9999px, 69px, 0);
    transform: skew(0.56deg);
  }
  85% {
    clip: rect(38px, 9999px, 94px, 0);
    transform: skew(0.83deg);
  }
  90% {
    clip: rect(73px, 9999px, 18px, 0);
    transform: skew(0.29deg);
  }
  95% {
    clip: rect(52px, 9999px, 36px, 0);
    transform: skew(0.67deg);
  }
  100% {
    clip: rect(27px, 9999px, 82px, 0);
    transform: skew(0.45deg);
  }
}

@keyframes glitch-anim2 {
  0% {
    clip: rect(65px, 9999px, 100px, 0);
    transform: skew(0.6deg);
  }
  5% {
    clip: rect(52px, 9999px, 74px, 0);
    transform: skew(0.27deg);
  }
  10% {
    clip: rect(79px, 9999px, 85px, 0);
    transform: skew(0.88deg);
  }
  15% {
    clip: rect(32px, 9999px, 46px, 0);
    transform: skew(0.15deg);
  }
  20% {
    clip: rect(91px, 9999px, 12px, 0);
    transform: skew(0.73deg);
  }
  25% {
    clip: rect(43px, 9999px, 67px, 0);
    transform: skew(0.39deg);
  }
  30% {
    clip: rect(18px, 9999px, 93px, 0);
    transform: skew(0.81deg);
  }
  35% {
    clip: rect(56px, 9999px, 28px, 0);
    transform: skew(0.04deg);
  }
  40% {
    clip: rect(84px, 9999px, 51px, 0);
    transform: skew(0.62deg);
  }
  45% {
    clip: rect(25px, 9999px, 77px, 0);
    transform: skew(0.33deg);
  }
  50% {
    clip: rect(68px, 9999px, 39px, 0);
    transform: skew(0.91deg);
  }
  55% {
    clip: rect(11px, 9999px, 95px, 0);
    transform: skew(0.19deg);
  }
  60% {
    clip: rect(47px, 9999px, 61px, 0);
    transform: skew(0.76deg);
  }
  65% {
    clip: rect(86px, 9999px, 22px, 0);
    transform: skew(0.48deg);
  }
  70% {
    clip: rect(34px, 9999px, 88px, 0);
    transform: skew(0.07deg);
  }
  75% {
    clip: rect(72px, 9999px, 42px, 0);
    transform: skew(0.69deg);
  }
  80% {
    clip: rect(19px, 9999px, 71px, 0);
    transform: skew(0.35deg);
  }
  85% {
    clip: rect(58px, 9999px, 16px, 0);
    transform: skew(0.94deg);
  }
  90% {
    clip: rect(82px, 9999px, 54px, 0);
    transform: skew(0.22deg);
  }
  95% {
    clip: rect(41px, 9999px, 89px, 0);
    transform: skew(0.58deg);
  }
  100% {
    clip: rect(63px, 9999px, 33px, 0);
    transform: skew(0.84deg);
  }
}

@keyframes glitch-skew {
  0% {
    transform: skew(0deg);
  }
  10% {
    transform: skew(-0.5deg);
  }
  20% {
    transform: skew(0.5deg);
  }
  30% {
    transform: skew(0deg);
  }
  40% {
    transform: skew(0.5deg);
  }
  50% {
    transform: skew(-0.3deg);
  }
  60% {
    transform: skew(0.3deg);
  }
  70% {
    transform: skew(0deg);
  }
  80% {
    transform: skew(-0.5deg);
  }
  90% {
    transform: skew(0.5deg);
  }
  100% {
    transform: skew(0deg);
  }
}

/* Cyber Grid */
.cyber-grid {
  background-image: 
    linear-gradient(rgba(0, 255, 65, 0.03) 1px, transparent 1px),
    linear-gradient(90deg, rgba(0, 255, 65, 0.03) 1px, transparent 1px);
  background-size: 50px 50px;
  animation: grid-move 20s linear infinite;
}

@keyframes grid-move {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 50px 50px;
  }
}

/* Hacker Typewriter Effect */
.hacker-typewriter {
  border-right: 2px solid transparent;
  animation: typewriter-cursor 1s step-end infinite;
}

@keyframes typewriter-cursor {
  50% {
    border-color: #00ff41;
  }
}

/* Spin animations */
@keyframes spin-slow {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes spin-slow-reverse {
  from {
    transform: rotate(360deg);
  }
  to {
    transform: rotate(0deg);
  }
}

.animate-spin-slow {
  animation: spin-slow 10s linear infinite;
}

.animate-spin-slow-reverse {
  animation: spin-slow-reverse 15s linear infinite;
}

/* Scroll down animation */
@keyframes scroll-down {
  0%, 100% {
    transform: translateY(0);
    opacity: 1;
  }
  50% {
    transform: translateY(16px);
    opacity: 0.3;
  }
}

.animate-scroll-down {
  animation: scroll-down 1.5s ease-in-out infinite;
}

