import { AdminLayout } from "@/layouts/AdminLayout";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { useMockData } from "@/context/MockContext";
import { useState } from "react";
import { 
  Plus, 
  Search, 
  Filter, 
  MoreVertical, 
  Shield,
  Trash,
  UserCog
} from "lucide-react";

export default function AdminUsers() {
  const { users, deleteUser, addUser, updateUser } = useMockData();
  const [editingRoleId, setEditingRoleId] = useState<number | null>(null);

  const handleDelete = (id: number) => {
    if (confirm("Are you sure you want to remove this user?")) {
      deleteUser(id);
    }
  };

  const handleAddDummy = () => {
    addUser({
      name: "New User",
      email: `user${Date.now()}@example.com`,
      role: "Editor",
      status: "Active",
      avatar: ""
    });
  };

  const changeRole = (id: number, newRole: string) => {
    updateUser(id, { role: newRole });
    setEditingRoleId(null);
  };

  const toggleStatus = (id: number, currentStatus: string) => {
    const newStatus = currentStatus === "Active" ? "Inactive" : "Active";
    updateUser(id, { status: newStatus });
  };

  return (
    <AdminLayout>
      <div className="space-y-6">
        <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
          <div>
            <h1 className="text-3xl font-heading font-bold">Users</h1>
            <p className="text-muted-foreground">Manage team access and roles</p>
          </div>
          <Button className="bg-primary hover:bg-primary/90" onClick={handleAddDummy}>
            <Plus className="w-4 h-4 mr-2" /> Invite User
          </Button>
        </div>

        <div className="flex flex-col sm:flex-row gap-4 bg-card p-4 rounded-lg border border-border">
          <div className="relative flex-1">
            <Search className="absolute left-2.5 top-2.5 h-4 w-4 text-muted-foreground" />
            <Input placeholder="Search users..." className="pl-9" />
          </div>
          <div className="flex gap-2">
            <Button variant="outline" className="gap-2">
              <Filter className="w-4 h-4" /> Role
            </Button>
          </div>
        </div>

        <div className="bg-card rounded-lg border border-border overflow-hidden">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>User</TableHead>
                <TableHead>Role</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Last Active</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {users.map((user) => (
                <TableRow key={user.id}>
                  <TableCell>
                    <div className="flex items-center gap-3">
                      <Avatar>
                        <AvatarImage src={user.avatar} />
                        <AvatarFallback>{user.name.charAt(0)}</AvatarFallback>
                      </Avatar>
                      <div>
                        <div className="font-medium">{user.name}</div>
                        <div className="text-xs text-muted-foreground">{user.email}</div>
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    {editingRoleId === user.id ? (
                      <select 
                        className="p-1 rounded border bg-background text-sm"
                        defaultValue={user.role}
                        onChange={(e) => changeRole(user.id, e.target.value)}
                        onBlur={() => setEditingRoleId(null)}
                        autoFocus
                      >
                        <option>Super Admin</option>
                        <option>Admin</option>
                        <option>Editor</option>
                        <option>Viewer</option>
                      </select>
                    ) : (
                      <div className="flex items-center gap-2 cursor-pointer" onClick={() => setEditingRoleId(user.id)}>
                        {user.role === "Super Admin" && <Shield className="w-3 h-3 text-primary" />}
                        <span>{user.role}</span>
                      </div>
                    )}
                  </TableCell>
                  <TableCell>
                    <Badge 
                      variant="outline" 
                      className={`cursor-pointer ${user.status === "Active" ? "text-green-500 border-green-500/20 bg-green-500/10" : "text-muted-foreground"}`}
                      onClick={() => toggleStatus(user.id, user.status)}
                    >
                      {user.status}
                    </Badge>
                  </TableCell>
                  <TableCell className="text-muted-foreground text-sm">{user.lastActive}</TableCell>
                  <TableCell className="text-right">
                    <div className="flex justify-end gap-2">
                      <Button variant="ghost" size="icon">
                        <UserCog className="w-4 h-4" />
                      </Button>
                      <Button variant="ghost" size="icon" className="text-destructive hover:text-destructive" onClick={() => handleDelete(user.id)}>
                        <Trash className="w-4 h-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      </div>
    </AdminLayout>
  );
}

