import { useState } from "react";
import { motion } from "framer-motion";
import { Mail, MapPin, Phone, Send, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { api } from "@/lib/api";

// Floating Label Input Component
const FloatingLabelInput = ({ 
  name, 
  value, 
  onChange, 
  label, 
  type = "text", 
  required = false 
}: { 
  name: string; 
  value: string; 
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void; 
  label: string; 
  type?: string; 
  required?: boolean; 
}) => {
  const [isFocused, setIsFocused] = useState(false);

  return (
    <div className="relative pt-4">
      <input
        type={type}
        name={name}
        value={value}
        onChange={onChange}
        required={required}
        onFocus={() => setIsFocused(true)}
        onBlur={() => setIsFocused(false)}
        className="peer w-full bg-transparent border-b-2 border-border py-2 text-foreground placeholder-transparent focus:outline-none focus:border-primary transition-colors"
        placeholder={label}
      />
      <label
        className={`absolute left-0 top-2 text-sm transition-all duration-300 pointer-events-none
          ${isFocused || value ? "-top-0 text-xs text-primary" : "top-6 text-muted-foreground"}
        `}
      >
        {label}
      </label>
    </div>
  );
};

const FloatingLabelTextarea = ({ 
  name, 
  value, 
  onChange, 
  label, 
  required = false 
}: { 
  name: string; 
  value: string; 
  onChange: (e: React.ChangeEvent<HTMLTextAreaElement>) => void; 
  label: string; 
  required?: boolean; 
}) => {
  const [isFocused, setIsFocused] = useState(false);

  return (
    <div className="relative pt-4">
      <textarea
        name={name}
        value={value}
        onChange={onChange}
        required={required}
