      messages,
      markAsRead,
      deleteMessage,
      activityLogs,
      notifications,
      isLoading,
      refetchAll
    }}>
      {children}
    </MockContext.Provider>
  );
}

export const useMockData = () => {
  const context = useContext(MockContext);
  if (!context) throw new Error("useMockData must be used within a MockProvider");
  return context;
};

